mapper.insert:
  /* com.goaltech.login.mapper.UserMapper.insertUser */
	INSERT INTO USER(
	USER_ID,
	ROLE_CODE,
	USER_PW,
	USER_NAME,
	USER_PHONE,
	JOINDATE,
	USER_STATUS,
	UPDATED_AT
	) VALUES(
	    #{user_id}
	    ,#{role_code}
	    ,#{user_pw}
	    ,#{user_name}
	    ,#{user_phone}
	    ,now()
	    ,#{user_status}
	    ,now()
	 )

mapper.select:
/* com.goaltech.login.mapper.UserMapper.selectUser */
	SELECT *
	FROM USER
	WHERE USER_ID = #{user_id}
	AND
	USER_PW = #{user_pw}


SELECT USER_PW
FROM USER
WHERE USER_ID = #{user_id}
